import React, { useState } from 'react'
import "./login.css";
import { setToAdd,addTOList } from '../Store/shopslice';
import { useDispatch } from 'react-redux';
function Login() {
const initalData={
    name:"",
    area:"",
    category:"",
    openDate:"",
    closeDate:""
}
const [FormData,setFormData]=useState(initalData);
const [otherOption,setOtherOption]=useState(false);
const [error,seterror]=useState({
    name:"",
    category:"",
    area:"",
    openDate:"",
    closeDate:"",
});
const  dispatch=useDispatch();

const inputHandler=(event)=>{
    const {name,value}=event.target;
    if(name==="category" && value==="other"){
         setOtherOption(true);
    }
    else if(name==="category" && value!=="other"){
          setOtherOption(false);
        // console.log(name,value);
    }
   setFormData({
     ...FormData,
     [name]:value
   })
}
const submitHandler=(e)=>{
    e.preventDefault();
    // let regex=/[1-9]/g;
    // if(FormData.name.match(regex) ){
    //     seterror({
    //         ...error,
    //         name:"name should only contain alphabets"
    //     })
        
       
    // }
    // if(!FormData.name){
    //     seterror({
    //         ...error,
    //         name:"Please enter name"
    //     })
        
    // }
    // if(!FormData.category){
    //     seterror({
    //         ...error,
    //         category:"please select a category"
    //     })
    // }
    // if(!FormData.area){
    //     seterror({
    //         ...error,
    //         area:"please select a categoru"
    //     })
    // }

    dispatch(addTOList(FormData));
    dispatch(setToAdd());
    
}
  return (
    <div className="login">
        <form className='login-form'> 
        <div className="shop-name">
            <label>Enter Shop Name</label>
            <input type="text" name="name" onChange={inputHandler} value={FormData.name}></input>
        <p className='error'>{error.name}</p>
        </div>
        <div className="select-area">
            <label>Select Area</label>
            <select name="area" id='area' onChange={inputHandler} value={FormData.area}>
                <option disabled selected value>Select an option</option>
                {/* <option value="thane">Thane</option> */}
                <option value="mumbai">Mumbai</option>
                <option value="pune">Pune</option>
                <option value="Nashik">Nashik</option>
                <option value="nagpur">nagpur</option>
            </select>
            <p className='error'>{error.area}</p>
        </div>
        <div className="select-category">
            <label>Select Category</label>
            <select name="category" id='category' onChange={inputHandler} value={FormData.category}>
                <option disabled select value >Select an option  </option>
                <option value="Grocery">Grocery</option>
                <option value="Baker">Baker</option>
                <option value="Butcher">Butcher</option>
                <option value="Chemist">Chemist</option>
                <option value="Stationary">Stationary</option>
                <option value="">other</option>
            </select>
            
            { otherOption  &&<input type="text" placeholder='enter other category' className='otherCategory' onChange={inputHandler} value={FormData.category} ></input>}
            <p className='error'>{error.category}</p>
        
        </div>
        <div className="opening-hrs">
            <label>Select Opening Date</label>
            <input type="date" name="openDate" onChange={inputHandler} value={FormData.openDate}></input>
        <p className='error'>{error.openDate}</p>
        </div>
        <div className="closing-hrs">
            <label>Select Closing Date</label>
            <input type="date" name="closeDate" onChange={inputHandler} value={FormData.closeDate}></input>
            <p className='error'>{error.closeDate}</p>
        </div>
        <button className='submitBtn' onClick={submitHandler}>Submit</button>
        </form>
    </div>
  )
}

export default Login
