import { createSlice } from "@reduxjs/toolkit";


const shopSlice=createSlice({
    name:"shop",
    initialState:{toAdd:false,itemlist:[],showList:[]},
    reducers:{
        setToAdd:(state)=>{state.toAdd=!state.toAdd},
        addTOList:(state,action)=>{console.log(action.payload)
            state.itemlist.push({
                name:action.payload.name,
                area:action.payload.area,
                category:action.payload.category,
                openDate:action.payload.openDate,
                closeDate:action.payload.closeDate,
            })
            state.showList=state.itemlist;
        },
        deleteToList:(state,action)=>{
            const  deleted =action.payload.name;
            // const deleted=action.payload;

            state.shw=state.itemlist.filter((item)=>item.name!==deleted);

            console.log(deleted);
        },
        searchtoList:(state,action)=>{
            const searchKey=action.payload;
            const searchList=state.itemlist;
           if(searchKey.length===0){
               state.itemlist=searchList;
           }
           else{
          state.itemlist.filter((item)=>{
              console.log(item.area.toLowerCase().startsWith('p'));
              return item.area.toLowerCase().startsWith(searchKey.toLowerCase()) ||item.category.toLowerCase().startsWith(searchKey.toLowerCase()) ||item.name.toLowerCase().startsWith(searchKey.toLowerCase());
            })
        }
        },
      
       
    }
})


export const {setToAdd,addTOList,deleteToList,searchtoList} =shopSlice.actions;
export default shopSlice.reducer;
