import React from 'react'
import "./Header.css";
import { searchtoList } from '../Store/shopslice';
import { useDispatch } from 'react-redux';
function Header() {
  const dispatch=useDispatch();
  const searchFilter=(event)=>{
    dispatch(searchtoList(event.target.value));
  }
  return (
    <div className="header">
        <h1 className='header-title'>Shop List</h1>
        
        {/* <input type="string" placeholder='search shop here' className='searchInput' onChange={searchFilter}></input> */}
        {/* <label>Select Category</label> */}
            <select name="category"  value={FormData.category}>
             <option value="" disabled>Select and Option</option>
                <option  value="other">Add other</option>
                <option value="Grocery">Grocery</option>
                <option value="Baker">Baker</option>
                <option value="Butcher">Butcher</option>
                <option value="Chemist">Chemist</option>
                <option value="Stationary">Stationary</option>
            </select>
    </div>
  )
}

export default Header
