import { createSlice } from "@reduxjs/toolkit";


const shopSlice=createSlice({
    name:"shop",
    initialState:{toAdd:false},
    reducers:{
        setToAdd:(state)=>{state.toAdd=state}
       
    }
})


export const {setToAdd} =shopSlice.actions;
export default shopSlice.reducer;
