import "./Main.css"
import Login from './Login'
function Main() {
const [showlogin,setShowLogin]=useState(false);

const addList=()=>{
    setShowLogin(true);
}
  return (
          <>
      <div className="main">
    <button className='addBtn'  onClick={addList}>Click to add</button>
  {showlogin && <Login></Login>}
      </div>
          </>
  )
}

export default Main
